		Scanner scanner = new Scanner(System.in);
		int storeCount = scanner.nextInt();
		for (int i = 0; i &lt; storeCount; i++) {  // A loop that repeats 5 times.
			// Choose one of the three solutions above and insert them here, I choose the do-while since it&#39;s the cleanest
			int storeSales;
			do {
