  System.out.println(&quot;Do you want to loop again?&quot;);
 cont = input1.next().charAt(0);
 }
 scanner.close();
 }
 static int fact(int n)
 {
   int output;
   if(n == 1){
     return 1;
   }

   output = fact(n - 1) * n;
   return output;

 }
 }
