    for (int i = 0; i &lt; keys.size(); i++) {
        multiMap.put(keys.get(i), values.get(i));
    }
    List&lt;T&gt; sortedValues = Lists.newArrayList();
    for (Integer key : Ordering.natural().sortedCopy(multiMap.keys())) {
