public static &lt;T&gt; Observable&lt;T&gt; replayDynamicSize(ReplaySubject&lt;T&gt; subject, int n) {
    return Observable.defer(() -&gt; {
         int s = Math.max(0, subject.size() - n);
         return skip &gt; 0 ? subject.skip(s) : subject;
    });
}

