    public boolean anagram(String s, String t) {
        if(s.length() != t.length())
            return false;

        int value = 0;
        for(int i = 0; i &lt; s.length(); i++){
            value += ((int)s.charAt(i))^2;
            value -= ((int)t.charAt(i))^2;
        }
        return value == 0;
    }


