        // Make the update so we can move the camera
        CameraUpdate cameraUpdate = CameraUpdateFactory.newCameraPosition(mCameraPosition);

        // Move the camera with or without animation.  The time we don&#39;t want
        // animation is when we&#39;re setting the position initially
        if (animate) {
            mGoogleMap.animateCamera(cameraUpdate);
