    			RequestSender sender = new RequestSender();
    			CyclicBarrier c1 = new CyclicBarrier(2, () -&gt; System.out.println(&quot;***  2 Threads worked !&quot;));
    
    			for (int i = 0; i &lt; 16; i++) {
