    public static void insert(String[] names, int i)
    {
        String temp = names[i];
        int j = i - 1;
        
        while (j &gt;= 0 &amp;&amp; names[j].compareToIgnoreCase(temp)  &gt; 0)
        {
            names[j + 1]= names[j];
            j--;
        }
        names[j + 1] = temp;
    }

public static void insertionSort(int... arr) {
    for (int i = 1; i &lt; arr.length; i++) {
        if (arr[i] &gt;= arr[i - 1])
            continue;

        int j = i - 1;

        for (; j &gt;= 0; j--)
            if (arr[j] &lt; arr[i])
                break;

        int tmp = arr[i];
        System.arraycopy(arr, j + 1, arr, j + 2, i - j - 1);
        arr[j + 1] = tmp;
    }
}

