      public static boolean hasCross(char[][] board, char c) {
        // Assumes a SQUARE board!
        boolean crossA = true;
        boolean crossB = true;
        for(int col=0, rowA=0, rowB=(board.length-1); col&lt;board.length; col++, rowA++, rowB--) {
          if (board[rowA][col]!=c) {
            crossA = false;
          }
          if (board[rowB][col]!=c) {
            crossB = false;
          }
        }
        return crossA || crossB;
      }

