			System.out.println(&quot;Your number wasn&#39;t found&quot;);
		} else {
			input2DArray[index.getRow()][index.getColumn()] = 0;
			System.out.println(&quot;Your number is in Row &quot; + index.getRow() + &quot; and Column &quot; + index.getColumn() + &quot;.&quot;);
		}

