	private Index findIndexOfGivenNumberIn2DArray(int arr[][], int numberToFind) {
		Index index = null;
		for (int row = 0; row &lt; arr.length; row++) {
			for (int column = 0; column &lt; arr[row].length; column++) {
				if (arr[row][column] == numberToFind) {
					index = new Index(row, column);
					return index;
				}
			}
		}

		return index;
	}

	private static class Index {
		private int row;
		private int column;

		public Index(int row, int column) {
			this.row = row;
			this.column = column;
		}

		public int getRow() {
			return row;
		}

		public int getColumn() {
