public class PrimeGenerator {
	private int num;
	private int prime;
	private int numbers = 0; 

	public PrimeGenerator(int num) {
		this.num = num; 
	}

	public int nextPrime(int num) {
		while(numbers &lt;= num) {
			if (isPrime(numbers)) {
				return numbers; 
			}
			numbers++; 
		}        
		return numbers; 
	}

	public boolean isPrime(int num) {
		if (num &lt;= 1) {
			return false; 
		} else if (num == 2)  {
