	private ArrayList&lt;String&gt; parseIntsAndFloats(String raw) {

		ArrayList&lt;String&gt; listBuffer = new ArrayList&lt;String&gt;();

		Pattern p = Pattern.compile(&quot;[0-9]*\\.?[0-9]+&quot;);

		Matcher m = p.matcher(raw);

		while (m.find()) {
			listBuffer.add(m.group());
		}

		return listBuffer;
	}

