```java
public class Queue {
    private Node start;

    public Queue(){
        start = null;
    }

    public boolean isEmpty() {
        return start == null;
    }

    public void enqueue(int element) {
        if (isEmpty()) {
            Node newNode = new Node(element);
            newNode.next = start;
            start = newNode;
        } else {
            Node current = start;
            while (current.next != null) {
                current = current.next;
            }
            current.next = new Node(element);
        }
    }

    public int dequeue() {
        int result = -1;

        if (isEmpty()) {
            System.err.println(&quot;Error: The queue is empty&quot;);
        } else {
            result = start.data;
            start = start.next;
        }

        return result;
    }

    public void display() {
        if (isEmpty()) {
            System.err.println(&quot;Error: The queue is empty&quot;);
        } else {
            Node current = start;
            System.out.print(&quot;\nQueue &quot;);
            while (current != null) {
                System.out.printf(&quot;|%d|&quot;, current.data);
                current = current.next;
            }
            System.out.print(&quot;\n&quot;);
        }
    }
