        View view = inflater.inflate(R.layout.fragment_profile, container, false);
        fAuth = FirebaseAuth.getInstance();
        currentUser = fAuth.getCurrentUser();

        // get all your view inside the onCreate itself since you have declared them globally
        profileUsername = view.findViewById(R.id.profileUser);
        profileEmail = view.findViewById(R.id.profileEmail);
        profilePassword = view.findViewById(R.id.profilePassword);
        profileImg = view.findViewById(R.id.profileImg);

        // These two are missing from your code
        titleEmail = view.findViewById(R.id.titleEmail);
        titleUsername = view.findViewById(R.id.titleUsername);

        updateNavHeader(); // crashing because it called and there is no parent
        return view; // here the parent is returned 

    }

    private void updateNavHeader() {
        
        
        titleEmail.setText(currentUser.getEmail());
        titleUsername.setText(currentUser.getDisplayName());
