                .setSubject((userPrincipal.getUsername()))
                .setIssuedAt(new Date())
                .setExpiration(new Date((new Date()).getTime() + jwtExpirationMs))
                .addClaims(addCustomClaims(userPrincipal))
                .signWith(key(), SignatureAlgorithm.HS256)
                .compact();
    }

    private Key key() {
        return Keys.hmacShaKeyFor(Decoders.BASE64.decode(jwtSecret));
    }
    public boolean validateJwtToken(String authToken) {
        try {
            Jwts.parserBuilder().setSigningKey(key()).build().parse(authToken);
            return true;
        } catch (MalformedJwtException e) {
            logger.error(&quot;Invalid JWT token: {}&quot;, e.getMessage());
        } catch (ExpiredJwtException e) {
            logger.error(&quot;JWT token is expired: {}&quot;, e.getMessage());
        } catch (UnsupportedJwtException e) {
            logger.error(&quot;JWT token is unsupported: {}&quot;, e.getMessage());
        } catch (IllegalArgumentException e) {
            logger.error(&quot;JWT claims string is empty: {}&quot;, e.getMessage());
        }

        return false;
    }

    public String getUserNameFromJwtToken(String token) {
        return Jwts.parserBuilder().setSigningKey(key()).build()
                .parseClaimsJws(token).getBody().getSubject();
    }
    private Map&lt;String, Object&gt; addCustomClaims(UserDetailsImpl userPrincipal) {
        Map&lt;String, Object&gt; customClaims=new HashMap&lt;&gt;();
        List&lt; UserRoleResponse&gt; userRoleResponse =userPrincipal.getAuthorities().stream()
                .map(userRole-&gt; {
                    UserRoleResponse roleResponse=new UserRoleResponse();
                    roleResponse.setName(userRole.getAuthority());
                    return roleResponse;
                }).toList();
        customClaims.put(&quot;userRoles&quot;, userRoleResponse);
        return customClaims;
    }
}
