            int xDiff = maxX - minX;
            int yDiff = maxY - minY;
            
            int sx1 = minX - 1;
            int sy1 = minY - 1;
            
            int sideDiff = Math.abs(xDiff - yDiff) + 1;
            
            int sx2; 
            int sy2;
            
            if(xDiff &gt; yDiff){
                sx2 = maxX + 1;
                sy2 = maxY + sideDiff;
            } else {
