            for(p = sx1; p &lt; sx2 - minSize; p++) {
                for(q = sy1; q &lt; sy2 - minSize; q++) {
                    int xd = sx2 - p; int yd = sy2 - q;
                    int sd = (xd &lt; yd)? xd : yd;
                    for(int i = sd; i &gt;= minSize; i--){
