    public static List&lt;String&gt; readFromPort(SerialPort serialPort) {

        Scanner sc = new Scanner(serialPort.getInputStream());

        List&lt;String&gt; line = new ArrayList&lt;&gt;();

        while (sc.hasNextLine()) {
            line.add(sc.next());
            if(line.contains(&quot;m3&quot;)){
                break;
            }
        }
        return line;
    }

