                setLayout(new GridBagLayout());
                GridBagConstraints gbc = new GridBagConstraints();
                gbc.gridx = 0;
                gbc.gridy = 0;
                gbc.insets = new Insets(8, 8, 8, 8);
                
                JLabel label = new JLabel(&quot;Image placeholder&quot;) {
