        }catch (IOException e) {
        }
        System.out.println(&quot;\n============ 2&quot;);
        try (JarFile jar = new JarFile(wrongJarFile)) {
            for (Enumeration&lt;JarEntry&gt; entries = jar.entries(); entries.hasMoreElements(); ) {
