						{
							Integer.parseInt(op);
							System.out.println(&quot;You typed an int but it isn&#39;t 1 or 2&quot;);
						}
						catch(NumberFormatException e)
						{
							if(op.length() &gt; 1) System.out.println(&quot;You typed a String&quot;);
