    public Map&lt;Integer, byte[]&gt; split(byte[] masterKey)
    {
        if (StringUtil.isBlankOrMissing(masterKey))
        {
            throw new IllegalArgumentException(&quot;The Master-Key must not be null or empty&quot;);
        }

        byte[][] subKeyValues = new byte[numberOfShares][masterKey.length];
        for (int i = 0; i &lt; masterKey.length; i++)
        {
            byte[] polynomial = gf256.generatePolynomialOfDegree(keyThreshold - 1, masterKey[i]);
            for (int x = 1; x &lt;= numberOfShares; x++)
            {
                subKeyValues[x - 1][i] = gf256.evaluatePolynomial(polynomial, (byte) x);
            }
        }

        Map&lt;Integer, byte[]&gt; subKeys = new HashMap&lt;&gt;();
        for (int i = 0; i &lt; subKeyValues.length; i++)
        {
            subKeys.put(i + 1, subKeyValues[i]);
        }

        return Collections.unmodifiableMap(subKeys);
    }

    public byte[] join(Map&lt;Integer, byte[]&gt; subKeys)
    {
        if (subKeys == null || subKeys.isEmpty())
        {
            throw new IllegalArgumentException(&quot;Sub-Keys must not be null&quot;);
        }

        final byte[] masterKey = new byte[((byte[])subKeys.values().toArray()[0]).length];
        for (int i = 0; i &lt; masterKey.length; i++)
        {
            byte[][] points = new byte[subKeys.size()][2];
            int j = 0;
            for (Entry&lt;Integer, byte[]&gt; entry : subKeys.entrySet())
            {
                points[j][0] = entry.getKey().byteValue();
                points[j][1] = entry.getValue()[i];
                j++;
            }

            masterKey[i] = gf256.interpolatePolynomials(points);
        }

        return masterKey;
    }

