                  val = nums.get(j) * nums.get(j+1);
                 }
                 else if(operations.get(j) == &#39;/&#39;){
                  val = nums.get(j) / nums.get(j+1);
                 }
                 else if(operations.get(j) == &#39;+&#39;){
                  val = nums.get(j) + nums.get(j+1);
                 }
                 else{
