Node reverse(Node head) {
  if(head == null) return null;
  
  Node curr = head;
  Node next = null;
  Node prev = null;
  
  while(curr!=null &amp;&amp; curr.data%2==0) {
    next = curr.next;
    curr.next = prev;
    prev = curr;
    curr = next;
  }
  
  if(curr!=head) {
    head.next = curr;
    curr = reverse(curr);
    return prev;
  } else {
    head.next = reverse(head.next);
    return head;
  }
}

