    class Node { 
        int data; 
        Node next; 
        Node(int d) 
        { 
            data = d; 
            next = null; 
        } 
    }  
    
    void pairWiseSwap() 
    {    
        Node temp = head; 
        while (temp != null &amp;&amp; temp.next!=null) { 
        	int k = temp.data;
        	int j = temp.next.data;
        	if(k%2==0 &amp;&amp; j%2==0) {
        		temp.data=j;
        		temp.next.data=k;
        		temp=temp.next.next;
        		continue;
        	}
            
            temp = temp.next; 
        }
              
    } 
    public void push(int new_data) 
