            final double[][] c;
            final int i;
            final int j;

            public Multi(int N, int i, int j, double[][] a, double[][] b, double[][] c) {
                this.N = N;
                this.i = i;
                this.j = j;
                this.a = a;
                this.b = b;
                this.c = c;
            }

            @Override
            public void run() {
                for (int k = 0; k &lt; N; k++) c[i][j] += a[i][k] * b[k][j];
            }
        }
    }

