	private static final String COMMAND = &quot;logcat -d -v time&quot;;


	public static void fetch(OutputStream out, boolean close) throws IOException {
		byte[] log = new byte[1024 * 2];
		InputStream in = null;
		try {
			Process proc = Runtime.getRuntime().exec(COMMAND);
			in = proc.getInputStream();
			int read = in.read(log);
			while (-1 != read) {
				out.write(log, 0, read);
				read = in.read(log);
			}
		}
		finally {
			if (null != in) {
				try {
					in.close();
				}
				catch (IOException e) {
					// ignore
				}
			}

			if (null != out) {
				try {
					out.flush();
					if (close)
						out.close();
				}
				catch (IOException e) {
					// ignore
				}
			}
		}
	}

	public static void fetch(File file) throws IOException {
