        seq(input);
    }

    private static void seq(int[] input) {
        long startTime = System.currentTimeMillis();

        int result = 0;
        for (int num : input) {
            result += num;
        }
        
        System.out.println(&quot;Result: &quot; + result);
        System.out.println(&quot;Sequential time: &quot; + Long.toString(System.currentTimeMillis() - startTime));
    }

