    public Object covertValue(Object obj) {
        if (obj instanceof Map&lt;String, Object&gt;) {
            return convertMap((Map&lt;String, Object&gt;) obj);
        } else if (obj instanceof List&lt;Object&gt;) {
            return convertList((List&lt;Object&gt;) obj);
        } else {
