    NAME=YOUR_NAME
    PIDFILE=/var/run/$NAME.pid
    RUN_AS=USER_TO_RUN
    COMMAND=/usr/bin/java -- -jar YOUR_JAR
    
    d_start() {
    	start-stop-daemon --start --quiet --background --make-pidfile --pidfile $PIDFILE --chuid $RUN_AS --exec $COMMAND
