	        textView = tView;
        }

        public void animateText(String string) {
	        sText = string;
	        index = 0;

            textView.setText(&quot;&quot;);
   
            new Handler().removeCallbacks(characterAdder);
            new Handler().postDelayed(characterAdder, mDelay);
