        }
        
        @TestConfiguration
        static class A {
            
            @Primary
            @Bean
            public RestTemplateBuilder restTemplateBuilder(@Value(&quot;${components.web-admin-portal.rest.schemas}&quot;)
                                                                   String webAdminPortal) {
                
                RestTemplate          restTemplate = new RestTemplate();
