    public class ExternalIPUtil {
    
        private static final Pattern IPV4_PATTERN = Pattern.compile(&quot;^(([01]?\\d\\d?|2[0-4]\\d|25[0-5])\\.){3}([01]?\\d\\d?|2[0-4]\\d|25[0-5])$&quot;);
    
