public static void executeDockerCommand(Connection connection, String command, ThreadController controller) throws Exception {

        String username = connection.getUsername();
        String host = connection.getMachineHost();
        int port = connection.getMachinePort();
        String password = connection.getPassword();

        Session session = null;
        ChannelExec channel = null;

        try {
            session = new JSch().getSession(username, host, port);
            session.setPassword(password);
            session.setConfig(&quot;StrictHostKeyChecking&quot;, &quot;no&quot;);
            session.connect();

            channel = (ChannelExec) session.openChannel(&quot;exec&quot;);
            channel.setCommand(command);
            ByteArrayOutputStream responseStream = new ByteArrayOutputStream();
            channel.setOutputStream(responseStream);
            channel.connect();
            String cachedResponseStream = responseStream.toString();
            String output = &quot;&quot;;
            while (channel.isConnected() &amp;&amp; controller.isContThread()) {
                Thread.sleep(100);
                if (!cachedResponseStream.equalsIgnoreCase(responseStream.toString())) {
                    output = cachedResponseStream = responseStream.toString();
                    if (output.length() &gt; OUTPUT_CHAR_LIMIT) {
                        output = cachedResponseStream.substring(cachedResponseStream.length() - OUTPUT_CHAR_LIMIT);
                    }
                    controller.setOutput(output);
                }
            }
        } finally {
            if (session != null) {
                session.disconnect();
            }
            if (channel != null) {
                channel.disconnect();
            }
        }
    }
}

