    public static void main(String... args) {
        System.out.println(&quot;    &quot; + maximumSubarraysEqSum(List.of(1, 2, 3)));
        System.out.println(&quot;    &quot; + maximumSubarraysEqSum(List.of(2, 2, 2, -2)));
        System.out.println(&quot;    &quot; + maximumSubarraysEqSum(List.of(3, 4)));
