    public static void writeSquares(int n){
        if(n&lt;1)
            throw new IllegalArgumentException();
        if (n==1)
            System.out.print(1 + &quot;,&quot;);
        else if (n % 2 == 1) {
            System.out.print(n*n + &quot;,&quot;);
            writeSquares (n - 1);
        } else {
            writeSquares (n - 1);
            System.out.print(n*n + &quot;,&quot;);
        }
    }

