        public static int count_mismatches(String seq1, String seq2) {
    
            int finalCounter = 0;
    
            for (int i = 0; i &lt; Math.min(seq1.length(), seq2.length()); i++) {
                char c1 = seq1.charAt(i);
    
                char c2 = seq2.charAt(i);
    
                if (c1 == &#39;A&#39;) {
                    if (c2 == &#39;T&#39;)
                        continue;
                    else
                        finalCounter++;
    
                } else if (c1 == &#39;T&#39;) {
