                        finalCounter++;
                } else if (c1 == &#39;C&#39;) {
                    if (c2 == &#39;G&#39;)
                        continue;
                    else
                        finalCounter++;
                } 
            }
            return finalCounter + (Math.abs(seq1.length() - seq2.length()));
    
        }
    }
