Here is **another implementation**, which does not extend LinkedHashSet because that&#39;s redundant. Instead it uses a HashSet and an ArrayList. 

    /**
     * @Author Mo. Joseph
     *
     * Allows you to call get with o(1) instead of o(n) to get an instance by index
     */
    public static final class $IndexLinkedHashSet&lt;E&gt; implements Set&lt;E&gt; {
            private final ArrayList&lt;E&gt; list = new ArrayList&lt;&gt;( );
            private final HashSet&lt;E&gt;   set  = new HashSet&lt;&gt;  ( );
    
            public synchronized boolean add(E e) {
                    if ( set.add(e) ) {
                            return list.add(e);
                    }
                    return false;
            }
    
            public synchronized boolean remove(Object o) {
                    if ( set.remove(o) ) {
                            return list.remove(o);
                    }
                    return false;
            }
    
            @Override
            public boolean containsAll(Collection&lt;?&gt; c) {
                    return set.containsAll(c);
            }
    
            public synchronized void clear() {
                    set.clear();
                    list.clear();
            }
    
            public synchronized E get(int index) {
                    return list.get(index);
            }
    
            public synchronized boolean removeAll(Collection&lt;?&gt; c) {
                    if ( set.removeAll(c) ) {
                            return list.removeAll(c);
                    }
                    return true;
            }
    
            public synchronized boolean retainAll(Collection&lt;?&gt; c) {
                    if ( set.retainAll(c) ) {
                            return list.retainAll(c);
                    }
                    return false;
            }
    
            public synchronized boolean addAll(Collection&lt;? extends E&gt; c) {
                    boolean modified = false;
                    for (E e : c)
                            if (add(e))
                                    modified = true;
                    return modified;
            }
    
            @Override
            public synchronized int size() {
                    return set.size();
            }
    
            @Override
            public synchronized boolean isEmpty() {
                    return set.isEmpty();
            }
    
            @Override
            public synchronized boolean contains(Object o) {
                    return set.contains(o);
            }
    
            @Override
            public synchronized Iterator&lt;E&gt; iterator() {
                    return list.iterator();
            }
    
            @Override
            public synchronized Object[] toArray() {
                    return list.toArray();
            }
    
            @Override
            public synchronized &lt;T&gt; T[] toArray(T[] a) {
                    return list.toArray(a);
            }
    }

