    private static void mergeSortIndexed(double[] x, int[] ix, int[] scratch, int lo, int hi) {
        if (lo == hi)
            return;
        int mid = (lo + hi + 1) / 2;
        mergeSortIndexed(x, ix, scratch, lo, mid - 1);
        mergeSortIndexed(x, ix, scratch, mid, hi);
        mergeIndexed(x, ix, scratch, lo, mid - 1, mid, hi);
