    private static void mergeIndexed(double[] x, int[] ix, int[] scratch, int lo1, int hi1, int lo2, int hi2) {
        int i = 0;
        int i1 = lo1;
        int i2 = lo2;
        int n1 = hi1 - lo1 + 1;
        while (i1 &lt;= hi1 &amp;&amp; i2 &lt;= hi2) {
