public static &lt;T&gt; Stream&lt;List&lt;T&gt;&gt; sliding(Stream&lt;T&gt; stream, int window) {
    Queue&lt;T&gt; queue = new LinkedList&lt;&gt;();
    return stream.dropWhile(item -&gt; {
        if (queue.size() &lt; window - 1) {
            queue.add(item);
            return true;
        }
        return false;
    }).map(item -&gt; {
