                    if (space &gt;= 2) {
                    xCount = 0;
                    oCount = 0;
                    blankCount = 0;
                    }
                }
            }

            // If it&#39;s the last turn and X has no chance to win then 
            // returns true which will equal a tie.
            if (blanks &lt;= 1 &amp;&amp; xChance == 0) {
                return true;
            }

