            if ((oChance &gt; 0 &amp;&amp; blanks &gt;= 2) || (xChance &gt; 0 &amp;&amp; blanks &gt;= 1)) {
                xChance = 0;
                oChance = 0;
                return false;
            }
            else {
                return true;
            }
        }
        return false;
    }

