        String strPath2 = &quot;/opt&quot;;

        Path path1 = Paths.get(strPath1);
        Path path2 = Paths.get(strPath2);

        List&lt;String&gt; inbetweenPaths = new ArrayList&lt;&gt;();
        List&lt;File&gt; inbetweenFiles = new ArrayList&lt;&gt;();

        if(path1.toFile().exists() &amp;&amp; path1.startsWith(path2)) {
