        [‚Ä¶]

        /**
         * sets the something
         *
         * @param something a nice value
         * @return {@code this}.
         */
        @java.lang.SuppressWarnings(&quot;all&quot;)
        public LombokJavadoc.LombokJavadocBuilder something(final String something) {
            this.something = something;
            return this;
        }
    }

    [‚Ä¶]

    /**
     * access for something
     *
     * @return a nice value
     */
