Queue&lt;String&gt; queue = new PriorityQueue&lt;String&gt;(100, new Comparator&lt;String&gt;() {
	public int compare(String s1, String s2) {
    	return Integer.parseInt(s1) - Integer.parseInt(s2);
	}
});
