// Singly-linked lists are already defined with this interface:
// class ListNode&lt;T&gt; {
//   ListNode(T x) {
//     value = x;
//   }
//   T value;
//   ListNode&lt;T&gt; next;
// }
//
boolean solution(ListNode&lt;Integer&gt; node) {
        ListNode&lt;Integer&gt; current = node;
        Deque&lt;Integer&gt; list = new ArrayDeque&lt;&gt;();

        while (current != null) {
            list.push(current.value);
            current = current.next;
        }

        current = node;
        while (!list.isEmpty()) {
            Integer el = list.poll();
            System.out.print(el + &quot; &quot;);
            if (el != current.value) {
                return false;
            }

            current = current.next;
        }

        return true;
}
