        }
        swap(getMin(), 0);
        size--;
        sink(0);
    }
    
    public void delMax() {
        if (isEmpty()) {
            throw new IllegalStateException(&quot;No Data Available to delete&quot;);
        }
        if (!reverse) {
            throw new RuntimeErrorException(null, &quot;You Initialized a Max heap&quot;);
        }
        swap(getMax(), 0);
        size--;
        sink(0);
    }
    
    public int size() {
